import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import WorkDurationChart from './WorkDurationChart';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const Dashboard = ({ data }) => {
  if (!data) return null;

  const { work_duration_breakdown } = data;

  // Prepare data for employment duration chart
  const durationData = {
    labels: ['1-14 Days', '15-30 Days', '31-40 Days', '41-50 Days', '51-60 Days', '61-70 Days', '71-80 Days', '81-99 Days', '100 Days'],
    datasets: [
      {
        label: 'Families Employed',
        data: [
          work_duration_breakdown['1_14_days'].households,
          work_duration_breakdown['15_30_days'].households,
          work_duration_breakdown['31_40_days'].households,
          work_duration_breakdown['41_50_days'].households,
          work_duration_breakdown['51_60_days'].households,
          work_duration_breakdown['61_70_days'].households,
          work_duration_breakdown['71_80_days'].households,
          work_duration_breakdown['81_99_days'].households,
          work_duration_breakdown['100_days'].households,
        ],
        backgroundColor: '#2E7D32',
      },
    ],
  };

  const durationOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Employment Duration Distribution',
      },
    },
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>{data.district} District</h2>
        <p>MGNREGA Performance Overview</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-number">
            {data.households_employed.toLocaleString()}
          </div>
          <div className="stat-label">Families Got Work</div>
        </div>
        
        <div className="stat-card">
          <div className="stat-number">
            {data.persondays_generated.toLocaleString()}
          </div>
          <div className="stat-label">Total Work Days Generated</div>
        </div>
        
        <div className="stat-card">
          <div className="stat-number">
            {Math.round(data.persondays_generated / data.households_employed)}
          </div>
          <div className="stat-label">Average Days per Family</div>
        </div>
        
        <div className="stat-card">
          <div className="stat-number">
            {work_duration_breakdown['100_days'].households.toLocaleString()}
          </div>
          <div className="stat-label">Families Completed 100 Days</div>
        </div>
      </div>

      <div className="charts-section">
        <div className="chart-container">
          <h3 className="chart-title">Work Duration Distribution</h3>
          <WorkDurationChart data={work_duration_breakdown} />
        </div>

        <div className="chart-container">
          <h3 className="chart-title">Families by Work Duration</h3>
          <Bar data={durationData} options={durationOptions} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
